INSERT INTO pergunta (id,texto,ajuda,disciplina_id) VALUES (105, 'Abordagem inicial de analytics ocorre por meio de análises diretamente conectadas nas bases transacionais do produto  em mais de 80% das iniciativas, interferindo no desempenho e na robustez do produto.', 'Explicação da pergunta', 7);
INSERT INTO pergunta (id,texto,ajuda,disciplina_id) VALUES (106, 'Sumarizações e análises externas de dados ocorrem em pelo menos 20% das bases de dados transacionais do produto.', 'Explicação da pergunta', 7);
INSERT INTO pergunta (id,texto,ajuda,disciplina_id) VALUES (107, 'Demandas OLAP ocorrem para os dados correspondentes a pelo menos 50% das transações críticas do produto.', 'Explicação da pergunta', 7);
INSERT INTO pergunta (id,texto,ajuda,disciplina_id) VALUES (108, 'Ferramentas de BI passam a ter utilização ampla no contexto do produto  sendo aplicadas em mais de 50% das atividades OLAP.', 'Explicação da pergunta', 7);
INSERT INTO pergunta (id,texto,ajuda,disciplina_id) VALUES (109, 'Os dados da aplicação são acessíveis via formas autônomas de acesso aos dados (como self-service BI) em pelo menos 50% das iniciativas de OLAP e começam a construir suas próprias integrações e dashboards, utilizando ferramentas homologadas pela TI.', 'Explicação da pergunta', 7);
INSERT INTO pergunta (id,texto,ajuda,disciplina_id) VALUES (110, 'Mais de 50% das iniciativas de analytics relacionadas ao produto estão dentro do escopo de desenvolvimento de modelos de Ciência de Dados.', 'Explicação da pergunta', 7);
INSERT INTO pergunta (id,texto,ajuda,disciplina_id) VALUES (111, 'Mais de 50% dos modelos de Ciência de Dados relacionados ao produto, já desenvolvidos ou em desenvolvimento, incorporam abordagem prescritiva com formas semi-automáticas para tomada de decisão.', 'Explicação da pergunta', 7);
INSERT INTO pergunta (id,texto,ajuda,disciplina_id) VALUES (112, 'Mais de 50% das iniciativas OLAP relacionadas ao produto são feitas diretamente pelos times de produto e de negócio com  autonomia para acesso aos dados, eventualmente  com suporte do time de analytics.', 'Explicação da pergunta', 7);
INSERT INTO pergunta (id,texto,ajuda,disciplina_id) VALUES (113, 'Mais de 50% das iniciativas de Ciência de Dados relacionadas ao produto, já implementadas ou em desenvolvimento, envolvem processos totalmente automáticos de otimização de decisões.', 'Explicação da pergunta', 7);
INSERT INTO pergunta (id,texto,ajuda,disciplina_id) VALUES (114, 'Mais de 80% das iniciativas de Ciência de Dados relacionadas ao produto evoluem usando técnicas de DevOps.', 'Explicação da pergunta', 7);
INSERT INTO pergunta (id,texto,ajuda,disciplina_id) VALUES (115, 'Os dados da aplicação permitem a autonomia para mais de 80% de trabalhos de OLAP relacionados ao produto com apoio mínimo da área de analytics.', 'Explicação da pergunta', 7);
INSERT INTO pergunta (id,texto,ajuda,disciplina_id) VALUES (116, 'Bases transacionais da aplicação não estão acopladas. (ex: mudanças na estrutura da base possuem baixo risco de problemas nos outros produtos.)', 'Explicação da pergunta', 7);
INSERT INTO pergunta (id,texto,ajuda,disciplina_id) VALUES (117, 'Bases transacionais não são consultadas  de forma ad hoc para tarefas analíticas, não compromentendo o desempenho e robustez das atividades transacionais da aplicação.', 'Explicação da pergunta', 7);
INSERT INTO pergunta (id,texto,ajuda,disciplina_id) VALUES (118, 'Bases de datawarehouse, com modelos dimensionais, são criadas para separar atividades de OLAP das atividades de OLTP para pelo menos 50% das atividades OLAP.', 'Explicação da pergunta', 7);
INSERT INTO pergunta (id,texto,ajuda,disciplina_id) VALUES (119, 'Cargas de ETL para integração de dados passam a ser desenvolvidas e agendadas de forma periódica e incremental para mais de 50% das atividades OLAP do produto, evitando impactar o desempenho de atividades OLTP.', 'Explicação da pergunta', 7);
INSERT INTO pergunta (id,texto,ajuda,disciplina_id) VALUES (120, 'Bases de dados com modelos não-relacionais (NoSQL) são utilizadas para armazenar de forma mais eficiente dados com estruturas que não se adequam ao modelo relacional.', 'Explicação da pergunta', 7);
INSERT INTO pergunta (id,texto,ajuda,disciplina_id) VALUES (121, 'Mais de 80% das atividades OLTP têm seus dados registrados em bases de dados desacopladas segundo contexto.', 'Explicação da pergunta', 7);
INSERT INTO pergunta (id,texto,ajuda,disciplina_id) VALUES (122, 'Mais de 80% das atividades de OLAP ocorrem em bases de datawarehouse separadas das atividades de OLTP do produto.', 'Explicação da pergunta', 7);
INSERT INTO pergunta (id,texto,ajuda,disciplina_id) VALUES (123, 'Cargas de ETL para integração de dados passam a ser desenvolvidas e agendadas de forma periódica e incremental para mais de 80% das atividades OLAP do produto, evitando impactar o desempenho de atividades OLTP.', 'Explicação da pergunta', 7);
INSERT INTO pergunta (id,texto,ajuda,disciplina_id) VALUES (124, 'Mais de 50% dos dados necessários para trabalhos de OLAP e Ciência de Dados relacionados ao produto já estão disponíveis para a análise pelas  áreas de negócio e times de produto, seja em formato dimensional após ETL ou como cópia dos dados originais, sem que seja necessário demandar novas cargas de ETL.', 'Explicação da pergunta', 7);
